import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

const App = () => {
  const [count, setCount] = useState(0)

  const add = () => {
    setCount(count + 1)
  }

  const substract = () => {
    if (count >= 0) {
      setCount(count - 1)
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        Counter result : {count}
        <button onClick={add}>+</button>
        <button onClick={substract}>-</button>
      </header>
    </div>
  );
}

export default App;
